// src/theme/theme.js
const blueTheme = {
  color: "blue",
  main: "#3AB2FF",
  sub: "#C5F8FF",
  button: "#6B95BC",
};

const greenTheme = {
  color: "green",
  main: "#77E092",
  sub: "#CAFBE7",
  button: "#00AC7C",
};

const pinkTheme = {
  color: "pink",
  main: "#FF82DF",
  sub: "#FFE5FF",
  button: "#F697C5",
};

export const theme = {
  blueTheme,
  greenTheme,
  pinkTheme,
};
