import styled from 'styled-components';
import { ThemeColorContext } from '../../context/context';
import { useContext, useState } from 'react';
import { Button } from './common';

const Layout = ({children}) => {
	const context = useContext(ThemeColorContext);
	const [mode, setMode] = useState(context.blueTheme);
	const handleMode = (e) => {
		const value = e.target.value;
		if(value === "blue"){
			setMode(context.blueTheme);
		} else if(value === "green"){
			setMode(context.greenTheme);
		} else if(value === "pink"){
			setMode(context.pinkTheme);
		}
	}
	return (
		<Wrapper>
			{/*mode={mode.main}*/}
			<Header mode={mode.main}>
				<Button value="blue" onClick={handleMode}>blue</Button>
				<Button value="green" onClick={handleMode}>green</Button>
				<Button value="pink" onClick={handleMode}>pink</Button>
			</Header>
			<div>{children}</div>
			<Footer mode={mode.main}>
				2025 LIKELION FE
			</Footer>
		</Wrapper>

	);
};

export default Layout

const Wrapper = styled.div`
  margin: 0;
  padding: 0;
  min-height: 100vh;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: space-between;
`;

const Header = styled.div`
  display: flex;
  height: 100px;
  width: 100%;
  justify-content: center;
  align-items: center;
  background-color: ${(props) => props.mode};
`;

const Footer = styled.div`
  display: flex;
  height: 50px;
  width: 100%;
  justify-content: center;
  align-items: center;
  background-color: ${(props) => props.mode};
`;
