import styled from "styled-components";
import { getMyPage } from "../apis/user";
import { useEffect, useState } from "react";

// 토큰을 이용해서 정보를 가져오는 페이지
const Mypage = () => {
    const [data, setData] = useState("");
    const [loading, setLoading] = useState(true);

    useEffect( () => {
            getMyPage(localStorage.getItem("access"))
    .then((data) => {
        setData(data);
        setLoading(false);
    })
    .catch((error) => {
        alert("토큰 기한 만료");
    })
}, []);

    return (
        <>
            <Wrapper>
                <Title>My page</Title>
                <div>이름 : {data.name} </div>
                <div>나이 : {data.age} </div>
            </Wrapper>
        </>
    );
};

export default Mypage;

const Wrapper = styled.div`
  display: flex;
  align-items: center;
  justify-items: center;
  flex-direction: column;
  border: 3px solid #89cdf6;
  padding: 30px;
  border-radius: 3%;
  font-size: 20px;
  width: 300px;
  div {
    font-size: 25px;
  }
`;

const Title = styled.div`
  font-size: 30px;
  font-weight: 700;
  margin-top: 15px;
  margin-bottom: 30px;
  color: #585858;
  font-family: SUITE;
  font-style: normal;
  font-weight: 800;
  line-height: normal;
`;
